import { Component } from '@angular/core';
import { BoxService } from '../box.service';
import { Router, ActivatedRoute } from '@angular/router';
import { Box } from '../class/box';

@Component({
  selector: 'app-box-upd',
  templateUrl: './box-upd.component.html',
  styleUrls: ['./box-upd.component.css']
})
export class BoxUpdComponent {
  box : Box ;

  constructor(private boxServ : BoxService,private router: Router,private route: ActivatedRoute){
    this.box = new Box();
  }
  onSubmit() {
    this.boxServ.update(this.box);
    this.gotoUserList();
  }
  ngOnInit() :void {
     this.boxServ.findById(this.route.snapshot.params['id']).subscribe(
      (responce) => {
        this.box = responce;
      }
     );


  }
  gotoUserList() {
    this.router.navigate(['/boxes']);
  }
}
<div class = "container">
  <div class = "row">
    <div class ="col-lg-6 col-md-6 col-sm-6 container justify-content-center card">
      <h1 class = "text-center"> Добавить бокс </h1>
      <div class = "card-body">
        <form (ngSubmit)="onSubmit()" method="POST">
          <div class ="form-group">
            <label> Название </label>
            <input
                    type = "text"
                    name = "brand"
                    [(ngModel)]="box.name"
                    class = "form-control"
                    placeholder="Семенов"
            />
          </div>

          <div class ="form-group">
            <select [(ngModel)]="box.type" class="form-control" >
              <option th:value="Грузовой">Грузовой</option>
              <option th:value="Легковой">Легковой</option>
            </select>
          </div>

          <div class = "form-group">
            <button type="submit" class = "btn btn-primary">
              Изменить
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
