import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CarServiceService } from '../car-service.service';
import { Car } from '../class/car';

@Component({
  selector: 'app-cars-upd',
  templateUrl: './cars-upd.component.html',
  styleUrls: ['./cars-upd.component.css']
})
export class CarsUpdComponent {
  car : Car = new Car();

  constructor(private carServ : CarServiceService,private router: Router,private route: ActivatedRoute){

  }
  onSubmit() {
    this.carServ.update(this.car);
    this.gotoUserList();
  }
  ngOnInit() :void {
    // this.carServ.findById(this.route.snapshot.url);
    console.log(this.route.snapshot.params);


  }
  gotoUserList() {
    this.router.navigate(['/']);
  }
}
<div class = "container">
  <div class = "row">
      <div class ="col-lg-6 col-md-6 col-sm-6 container justify-content-center card">
          <h1 class = "text-center"> Добавить машину </h1>
          <div class = "card-body">
              <form (ngSubmit)="onSubmit()" method="PUT">
                  <div class ="form-group">
                      <label> Марка </label>
                      <input
                              type = "text"
                              name = "brand"
                              [(ngModel)]="car.brand"
                              class = "form-control"
                              placeholder="марка"
                      />
                  </div>

                  <div class ="form-group">
                      <label> Номер </label>
                      <input
                              type = "text"
                              name = "number"
                              [(ngModel)]="car.number"
                              class = "form-control"
                              placeholder="А000АА00"
                      />
                  </div>

                  <div class ="form-group">
                      <label> Статус </label>
                      <select name="group" [(ngModel)]="car.status" class="form-control" >
                          <option th:value="Мытая">Мытая</option>
                          <option th:value="Грязная">Грязная</option>
                      </select>
                  </div>

                  <div class = "box-footer">
                      <button type="submit" class = "btn btn-primary">
                          Создать
                      </button>
                  </div>
              </form>
          </div>
      </div>
  </div>
</div>
