import { Component, OnInit } from '@angular/core';
import { PriemService } from '../priem.service';
import { Router } from '@angular/router';
import { Priem } from '../class/priem';
import * as moment from 'moment';
import { AppService } from '../app.service';
@Component({
  selector: 'app-priem-list',
  templateUrl: './priem-list.component.html',
  styleUrls: ['./priem-list.component.css']
})
export class PriemListComponent implements OnInit{
  priems? : Priem[];
  constructor(public app: AppService, private washServ : PriemService,private router: Router){
  }

  ngOnInit() :void {
   this.washServ.findAll().subscribe(data => {
     this.priems = data;
   });
 }
 remove(id : number){
   this.washServ.deletePriem(id).subscribe(() =>{
     this.router.navigate(['/redirect_priem']);
     this.ngOnInit();
   }
);

 }
 retDate(date : Date){
  return moment(date).format('DD MMMM YYYY');;
 }
}
<table class="table table-striped table-bordered">
  <thead class="table-dark">
  <tr>
    <th> Дата</th>
    <th> Врач</th>
    <th> Пациент</th>
    <th [hidden]="!this.app.authAdmin"> Действия</th>
  </tr>
  </thead>
  <tbody>
  <tr *ngFor="let priem of priems">
    <td >{{retDate(priem.date)}}</td>
    <td >{{priem.doctor.lastname}}</td>
    <td >{{priem.patient.lastname}}</td>
    <td [hidden]="!this.app.authAdmin">
      <a (click)="remove(priem.id)" class="btn btn-danger">Удалить </a>
    </td>
  </tr>
  </tbody>
</table>
