import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DoctorServiceService } from '../doctor-service.service';
import { Doctor } from '../class/doctor';

@Component({
  selector: 'app-doctors-upd',
  templateUrl: './doctors-upd.component.html',
  styleUrls: ['./doctors-upd.component.css']
})
export class DoctorsUpdComponent {
  doctor : Doctor ;

  constructor(private carServ : DoctorServiceService,private router: Router,private route: ActivatedRoute){
    this.doctor = new Doctor();
  }
  onSubmit() {
    this.carServ.update(this.doctor);
    this.gotoUserList();
  }
  ngOnInit() :void {
     this.carServ.findById(this.route.snapshot.params['id']).subscribe(
      (responce) => {
        this.doctor = responce;
      }
     );


  }
  gotoUserList() {
    this.router.navigate(['/']);
  }
}
<div class="modal fade" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
    <div class = "modal-dialog">
        <div class ="modal-content">
            <h1 class = "modal-title"> Изменить врача</h1>
            <div class = "modal-body">
                <form (ngSubmit)="onSubmit()" method="PUT">
                    <div class ="form-group">
                        <label> Фамилия </label>
                        <input
                                type = "text"
                                name = "lastname"
                                [(ngModel)] = "doctor.lastname"
                                class = "form-control"
                                placeholder="Петров"
                        />
                    </div>

                    <div class ="form-group">
                        <label> Имя </label>
                        <input
                                type = "text"
                                name = "firstname"
                                [(ngModel)] = "doctor.firstname"
                                class = "form-control"
                                placeholder="Иван"
                        />
                    </div>
                    <div class ="form-group">
                        <label> Отчество </label>
                        <input
                                type = "text"
                                name = "middlename"
                                [(ngModel)] = "doctor.middlename"
                                class = "form-control"
                                placeholder="Иванович"
                        />
                    </div>

                    <div class ="form-group">
                        <label> Статус </label>
                        <select name="type" [(ngModel)] = "doctor.type" class="form-control" >
                            <option th:value="Терапевт">Терапевт</option>
                            <option th:value="Офтальмолог">Офтальмолог</option>
                            <option th:value="Хирург">Хирург</option>
                            <option th:value="Кардиолог">Кардиолог</option>
                        </select>
                    </div>

                    <div class = "modal-footer">
                        <button type="submit" class = "btn btn-primary">
                            Изменить
                        </button>
                    </div>
                </form>
            </div>
        </div>
    </div>
  </div>
