import { Component, OnInit } from '@angular/core';
import { WashService } from '../wash.service';
import { Router } from '@angular/router';
import { BoxService } from '../box.service';
import { Box } from '../class/box';
import { Wash } from '../class/wash';

@Component({
  selector: 'app-wash-car',
  templateUrl: './wash-car.component.html',
  styleUrls: ['./wash-car.component.css']
})
export class WashCarComponent {
  boxes! : Box[];
  wash : Wash = new Wash();
  constructor(private washServ : WashService,private boxServ : BoxService,private router: Router){

  }
  ngOnInit() : void{
    this.boxServ.findAll().subscribe((data) => {
      this.boxes = data;
   });
  }
  onSubmit() {
    this.washServ.save(this.wash);
    this.gotoUserList();
  }

  gotoUserList() {
    this.router.navigate(['/redirect_wash']);
  }
}
<div class = "container">
  <div class = "row">
    <div class ="col-lg-6 col-md-6 col-sm-6 container justify-content-center card">
      <h1 class = "text-center"> Помыть машину </h1>
      <div class = "card-body">
        <form (ngSubmit)="onSubmit()" method="POST">
<!--          <div class ="form-group">-->
<!--            <label> Номер </label>-->
<!--            <p-->
<!--                    type = "text"-->
<!--                    name = "car"-->
<!--                    th:text = "${car.number}"-->
<!--                    class = "form-control"-->
<!--                    placeholder="a111aa11"-->
<!--            />-->
<!--          </div>-->

          <div class ="form-group">
            <label> Дата </label>
            <input
                    type = "date"
                    name = "date"
                    [(ngModel)]="wash.date"
                    class = "form-control"
                    placeholder="2000-12-30"
            />
          </div>


            <div class ="form-group">
            <label> Бокс для мойки </label>
                <select name="place" [(ngModel)]="wash.place" class="form-control" >
                     <option *ngFor="let box of boxes" value="box.id"  selected="1">{{box.name}}</option>
                </select>
            </div>

          <div class = "box-footer">
            <button type="submit" class = "btn btn-primary">
              Помыть
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
