import { Component } from '@angular/core';
import { BoxService } from '../box.service';
import { Router } from '@angular/router';
import { Box } from '../class/box';

@Component({
  selector: 'app-box-add',
  templateUrl: './box-add.component.html',
  styleUrls: ['./box-add.component.css']
})
export class BoxAddComponent {
  box : Box = new Box();
  constructor(private boxServ : BoxService,private router: Router){

  }
  onSubmit() {
    this.boxServ.save(this.box);
    this.gotoUserList();
  }

  gotoUserList() {
    this.router.navigate(['/redirect_box']);
  }
}
<div class = "container">
  <div class = "row">
      <div class ="col-lg-6 col-md-6 col-sm-6 container justify-content-center card">
          <h1 class = "text-center"> Добавить бокс </h1>
          <div class = "card-body">
<form (ngSubmit)="onSubmit()" method="POST">
  <div class ="form-group">
      <label> Название </label>
      <input
              type = "text"
              name = "brand"
              [(ngModel)]="box.name"
              class = "form-control"
              placeholder="Название"
      />
  </div>

  <div class ="form-group">
      <select [(ngModel)]="box.type" class="form-control" >
          <option th:value="Грузовой">Грузовой</option>
          <option th:value="Легковой">Легковой</option>
      </select>
  </div>

  <div class = "form-group">
  <button type="submit" class = "btn btn-primary">
      Создать
  </button>
  </div>
</form>
          </div>
      </div>
  </div>
</div>
