import { Component, OnInit } from '@angular/core';
import { DoctorServiceService } from '../doctor-service.service';
import { Doctor } from '../class/doctor';
import { Router } from '@angular/router';
import { delay } from 'rxjs';
import { AppService } from '../app.service';
import {BsModalRef, BsModalService, ModalOptions} from 'ngx-bootstrap/modal'
import { DoctorsUpdComponent } from '../doctors-upd/doctors-upd.component';
import { WebsocketService } from '../websocket.service';
@Component({
  selector: 'app-doctors-list',
  templateUrl: './doctors-list.component.html',
  styleUrls: ['./doctors-list.component.css']
})
export class DoctorsListComponent implements OnInit{
 doctors? : Doctor[];
 bsModalRef?: BsModalRef;
 constructor(public app: AppService,  private doctorService : DoctorServiceService,private router: Router, private modalService: BsModalService, private webSocketService:WebsocketService){
 }

 ngOnInit() :void {
  this.doctorService.findAll().subscribe(data => {
    this.doctors = data;
  });
  this.webSocketService.connectDoctor().subscribe((data) => {
    this.doctors = data;
  })
}
remove(id : number){
  this.doctorService.deleteDoctor(id).subscribe(() =>{
    this.router.navigate(['/redirect_doctor']);
    this.ngOnInit();
  }
);

}
openModal(){
  const modalOptions: ModalOptions = {
    initialState:{},
    class: 'modal-dialog-centered'
  }
  this.bsModalRef = this.modalService.show(DoctorsUpdComponent);
  this.ngOnInit();
}
}
<table class="table table-striped table-bordered">
    <thead class="table-dark">
    <tr>
        <th> Фамилия</th>
        <th> Имя</th>
        <th> Отчество</th>
        <th>Тип</th>
        <th [hidden]="!this.app.authAdmin"> Действия</th>
    </tr>
    </thead>
    <tbody>
    <tr *ngFor ="let doctor of doctors">
        <td >{{doctor.lastname}}</td>
        <td >{{doctor.firstname}}</td>
        <td >{{doctor.middlename}}</td>
        <td>{{doctor.type}}</td>
        <td [hidden]="!this.app.authAdmin">
            <a routerLink="/newpriem/{{doctor.id}}"
               class="btn btn-success">Принять</a>
            <a  (click)="openModal()"
               class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#staticBackdrop">Изменить</a>
            <a (click)="remove(doctor.id)"
               class="btn btn-danger">Удалить </a>
        </td>
    </tr>
    </tbody>
  </table>
