import { Component, OnInit } from '@angular/core';
import { PriemService } from '../priem.service';
import { ActivatedRoute, Router } from '@angular/router';
import { PatientService } from '../patient.service';
import { Patient } from '../class/patient';
import { Priem } from '../class/priem';
import { DoctorServiceService } from '../doctor-service.service';
import { Doctor } from '../class/doctor';

@Component({
  selector: 'app-priem-doctor',
  templateUrl: './priem-doctor.component.html',
  styleUrls: ['./priem-doctor.component.css']
})
export class PriemDoctorComponent {
  boxes! : Patient[];
  wash : Priem = new Priem();
  car! : Doctor;
  box! : Patient;
  id! : number;
  constructor(private washServ : PriemService,private boxServ : PatientService,private carServ : DoctorServiceService,private router: Router,private route :ActivatedRoute){

  }
  ngOnInit() : void{
    this.boxServ.findAll().subscribe((data) => {
      this.boxes = data;
   });
   this.carServ.findById(this.route.snapshot.params['id']).subscribe(
    (responce) => {
      this.car = responce;
    }
   );
  }
  onSubmit() {

     this.boxServ.findById(this.id).subscribe(
      (responce) => {

        this.box = responce;
        this.wash.car = this.car;
         this.wash.box = this.box;
         //this.wash.active = true;
        this.washServ.save(this.wash);
      }
     )



    this.gotoUserList();
  }

  gotoUserList() {
    this.router.navigate(['/']);
  }
}
<div class = "container">
  <div class = "row">
    <div class ="col-lg-6 col-md-6 col-sm-6 container justify-content-center card">
      <h1 class = "text-center"> Принять пациента </h1>
      <div class = "card-body">
        <form (ngSubmit)="onSubmit()" method="POST">  
          <div class ="form-group">
            <label> Дата </label>
            <input
                    type = "date"
                    name = "date"
                    [(ngModel)]="wash.date"
                    class = "form-control"
                    placeholder="2000-12-30"
            />
          </div>


            <div class ="form-group">
            <label> Бокс для мойки </label>
                <select name="place" [(ngModel)]="id" class="form-control" >
                     <option *ngFor="let box of boxes" value="{{box.id}}"  selected="1">{{box.lastname}}</option>
                </select>
            </div>

          <div class = "box-footer">
            <button type="submit" class = "btn btn-primary">
              Принять
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
