import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AppService {
 public static authenticated = false;
 public static authAdmin = false;
 response : any;
  constructor(private http: HttpClient) {
  }

  authenticate(credentials: { username: string; password: string; }) {
        console.log(credentials);

        this.http.post('http://localhost:8082/vhod',credentials).subscribe(response => {
            this.response = response;

            if (this.response['role']) {
              AppService.authAdmin = true;
          } else {
              AppService.authAdmin = false;
          }
            if (this.response['auth']) {
                AppService.authenticated = true;
            } else {
                AppService.authenticated = false;
            }
            console.log(AppService.authenticated + ' '+AppService.authAdmin);

        });

    }
}
