import { Component } from '@angular/core';
import { PatientService } from '../patient.service';
import { Router, ActivatedRoute } from '@angular/router';
import { Patient } from '../class/patient';

@Component({
  selector: 'app-patient-upd',
  templateUrl: './patient-upd.component.html',
  styleUrls: ['./patient-upd.component.css']
})
export class PatientUpdComponent {
  box : Patient ;

  constructor(private boxServ : PatientService,private router: Router,private route: ActivatedRoute){
    this.box = new Patient();
  }
  onSubmit() {
    this.boxServ.update(this.box);
    this.gotoUserList();
  }
  ngOnInit() :void {
     this.boxServ.findById(this.route.snapshot.params['id']).subscribe(
      (responce) => {
        this.box = responce;
      }
     );


  }
  gotoUserList() {
    this.router.navigate(['/boxes']);
  }
}
<div class="container">
  <div class="row">
    <div
      class="col-lg-6 col-md-6 col-sm-6 container justify-content-center card">
      <h1 class="text-center">Изменить пациента</h1>
      <div class="card-body">
        <form (ngSubmit)="onSubmit()" method="PUT">
            <div class ="form-group">
                <label> Фамилия </label>
                <input
                        type = "text"
                        name = "lastname"
                        [(ngModel)]="box.lastname"
                        class = "form-control"
                        placeholder="Иванов"
                />
            </div>
            <div class ="form-group">
                <label> Имя </label>
                <input
                        type = "text"
                        name = "firstname"
                        [(ngModel)]="box.firstname"
                        class = "form-control"
                        placeholder="Иван"
                />
            </div>
            <div class ="form-group">
                <label> Отчество </label>
                <input
                        type = "text"
                        name = "middlename"
                        [(ngModel)]="box.middlename"
                        class = "form-control"
                        placeholder="Иванович"
                />
            </div>

          <div class="form-group">
            <button type="submit" class="btn btn-primary">Изменить</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
