import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { AppService } from '../app.service';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent {
  credentials = {username: '', password: ''};
  constructor(public app: AppService, private http: HttpClient) {
  }
  login(){
    this.app.authenticate(this.credentials, () => {
  });
  return false;
  }
  logout(){
    this.app.authenticated = false;
  }
}
<div style="display: flex" class="navbar navbar-expand-lg navbar-dark bg-primary " th:fragment="navi(activeTab)">
  <div [hidden]="this.app.authenticated" >
      <form (sumbit)="login()" method="POST">
          <div class="input-group-prepend">
              <input required placeholder="Login" name="username"  [(ngModel)]="credentials.username"  aria-describedby="inputGroup-sizing-sm" style="margin-right: 10px; border-radius: 5px;">
              <input required type="password" placeholder="Password" [(ngModel)]="credentials.password" name="password" aria-describedby="inputGroup-sizing-sm"  style="margin-right: 10px ; border-radius: 5px;">
              <button type="submit"  class="btn btn-primary" style="background-color: darkblue" >Войти</button>
          </div>
      </form>
  </div>
  <div sec:authorize="isAuthenticated()"  class="nav-item" style="margin-right: 5px;">
      <form  method="POST">
          <input style="margin-left:auto;" (click)="logout()" type="submit" class="btn btn-danger" value="Logout"/>
      </form>
  </div>

<div sec:authorize="hasRole('USER')" >
  <a  sec:authorize="hasRole('ADMIN')" routerLink="/newcar" routerLinkActive="active-link" class="btn btn-light">Добавить новую машину</a>
  <a sec:authorize="hasRole('ADMIN')" routerLink="/newbox" routerLinkActive="active-link" class="btn btn-light">Добавить бокс</a>
  <a routerLink="/" routerLinkActive="active-link" class="btn btn-light">Автомобили</a>
  <a routerLink="/boxes" routerLinkActive="active-link" class="btn btn-light">Все боксы</a>
  <a routerLink="/washes" routerLinkActive="active-link" class="btn btn-light">История моек</a>
</div>
</div>
