import React from 'react';
import axios from 'axios';

export default class SystemList extends React.Component {
  ish;
  //https://new.vyatsu.ru/sveden/common/
  //https://www.vyatsu.ru/internet-gazeta/nashi-novosti.html
  componentDidMount() {
    axios.get(`https://new.vyatsu.ru/sveden/common/`)
        .then(res => {
            const tp = res.data;
            this.ish = tp;
            var dc =new DOMParser().parseFromString(this.ish,"text/html");
            //var dd = dc.getElementsByClassName('container')[0];
            // var ff = dd.getElementsByTagName('table')[4].getElementsByTagName('td');
            //var fb = ff.getElementsByTagName('td');
            //var container = document.getElementById("my_container"); // замените "my_container" на id вашего контейнера
            var div = document.createElement("div");

            var table = dc.getElementsByTagName("table")[4];
            var rows = table.rows;
            for (var i = 0; i < rows.length; i++) {
              var cells = rows[i].cells;
              for (var j = 0; j < cells.length; j++) {
                var cellValue = cells[j].innerHTML;
                //document.getElementById("container").innerText +=cellValue.textContent + '\n';
                console.log(cellValue);
                div.insertAdjacentHTML("beforeend", cellValue + "<br>");
              }
            }
            //dd.appendChild(div);
            // for(let i = 0; i < ff.length; i++){
            //   //const link = ff.href; // создаем новый элемент ссылки
            //   //link.href = 'http://google.com'; // задаем ссылку, например, в зависимости от индекса объекта
            //   //link.textContent = 'Ссылка'; // задаем текст ссылки
            //   //ff.children[i].appendChild(link);
            //   document.getElementById("container").innerText +=ff[i].textContent + '\n';
            // }
            
      })
      
  }
  render() {
    return (
      <div id='container' class="cont">
        
      </div>
    )
  }
}
